import React from 'react'
import { Grid,Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Image } from './Image';
import { Details } from './Details';


const useStyles = makeStyles({

    cardGrid:{
      
        marginTop:'30px',
        padding:'20px',
        backgroundColor: 'hsl(180, 31%, 95%)',
        boxShadow: '0px 6px 19px -8px hsl(180, 29%, 50%)',
        borderRadius: '5px'
      
    },

    b1:{
        border:'1px solid red'
    }

    ,b2:{
        border:'1px solid blue'
    }

    ,b3:{
        border:'1px solid green'
    }
 
    
});


export const JobCard = ({job}) => {

 
    const classes = useStyles();
    const {company,location,contract,new1,featured,position,postedAt,logo} = job;



    return (
        <div>
 
 
            <Box className={classes.cardGrid} >
                <Grid container direction="row" alignItems="center"  spacing={6}  >

                    <Image  path={logo} desc={company}/>
                    <Details 
                            company={company} 
                            new1={new1} 
                            featured={featured}
                            position={position}
                            dateAt={postedAt}
                            contract={contract}
                            location={location}
                    />

                   

                    <Grid item md={6} xs={12} >
                        <p>
                        {contract}
                        </p>
                       
                    </Grid>

                </Grid>
            </Box>
            
            
           

        </div>
    )
}
