import React from 'react'
import { Grid,Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Image } from './Image';


const useStyles = makeStyles({

    cardGrid:{
      
        marginTop:'30px',
        padding:'20px',
        backgroundColor: 'hsl(180, 31%, 95%)',
        boxShadow: '0px 6px 19px -8px hsl(180, 29%, 50%)',
        borderRadius: '5px'
      
    },

    b1:{
        border:'1px solid red'
    }

    ,b2:{
        border:'1px solid blue'
    }

    ,b3:{
        border:'1px solid green'
    }
 
    
});


export const JobCard = ({job}) => {

 
    const classes = useStyles();
    const {company,contract,level,logo} = job;



    return (
        <div>
 
 
            <Box className={classes.cardGrid} >
                <Grid container direction="row" alignItems="center"  spacing={6}  >

                    <Image className={classes.b1} path={logo} desc={company}/>

                    <Grid  item md={3} xs={12} >
                        {level}
                    </Grid>

                    <Grid item md={6} xs={12} >
                        <p>
                        {contract}
                        </p>
                       
                    </Grid>

                </Grid>
            </Box>
            
            
           

        </div>
    )
}
