import React from 'react';
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';



const useStyles = makeStyles( theme => {

    return{
        skill:{
        
            cursor:'pointer',
            color: 'hsl(180, 29%, 50%)',
            fontWeight:'700',
            fontSize:'14px',
            borderRadius:'3px',
            backgroundColor: 'hsl(180, 52%, 96%)',
            padding:'8px',
            [theme.breakpoints.down('xs')]:{
                justifyContent:'flex-start'
             }
          
        },
    
        layout:{
            display:'flex',
            gap:'20px',
            justifyContent:'flex-end',
            [theme.breakpoints.down('sm')]:{
               justifyContent:'flex-start'
            }
    
        }
    }
 

});

export const Skills = ({lang,tools,role,level}) => {


    const {skill,layout} = useStyles();
    const langTools = [...lang, ...tools];


    return (

            <Grid item md={7} xs={12} className={layout}>
                {
                    (langTools) 
                        ?
                            langTools.map((lang,i) => 
                                
                                <span  className={skill} key={i}>
                                     {lang}
                                </span>
                                )

                        :  ''
                                
                }
            </Grid>
    )
}
