import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import imgHeader from '../assets/images/bg-header-desktop.svg';
import imgMobile from '../assets/images/bg-header-mobile.svg';


const useStyles = makeStyles( them => {

    return{

        imgHead:{
            width:'100%',
            height:'100%',
            backgroundColor:'hsl(180, 29%, 50%)',
            [them.breakpoints.down('sm')]:{
                height:'100%',
                backgroundImage: `url(${imgMobile})`
             }
        }

    
    }

});




export const Header = () => {

    const classes = useStyles();

    return (
        <header >
            <img src={imgHeader} className={classes.imgHead} alt="img-header" />
        </header>
    )
}
