import React from 'react';
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';



const useStyles = makeStyles({

    skill:{
        
    
        cursor:'pointer',
        color: 'hsl(180, 29%, 50%)',
        fontWeight:'700',
        fontSize:'14px',
        borderRadius:'3px',
        backgroundColor: 'hsl(180, 52%, 96%)',
        padding:'8px',
        border:'1px solid red'
        
      
    },

    layout:{
        display:'flex',
        gap:'20px',
        justifyContent:'flex-end'

    }

});

export const Skills = ({lang,tools,role,level}) => {


    const {skill,layout} = useStyles();
    const langTools = [...lang,...tools];


    return (

            <Grid item md={4} xs={12} className={layout}>
                {
                    (langTools) 
                        ?
                            langTools.map((lang,i) => 
                                
                                <span  className={skill} key={i}> {lang}</span>
                                )

                        :  ''
                                
                }
            </Grid>
    )
}
