import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import imgHeader from '../assets/images/bg-header-desktop.svg';



const useStyles = makeStyles( them => {

    return{

        // imgHead:{
        //     width:'100%',
        //     height:'100%',
        //     backgroundColor:'hsl(180, 29%, 50%)',
        //     [them.breakpoints.down('xs')]:{
        //         height:'200px'
        //      }
        // }

        imgHead:{
            backgroundColor:'hsl(180, 29%, 50%)',
            height: 'calc(0.2 * 100vh)',
            backgroundImage: 'url("/images/bg-header-desktop.svg")',
            backgrondSize: 'cover',
            backgroundRepeat: 'no-repeat',
            backgroundPosition:'bottom',
            width:'100vw'

        }

    
    }

});




export const Header = () => {

    const {imgHead} = useStyles();

    return (
        <header >

            <div className={imgHead}></div>
            {/* <img src={imgHeader} className={imgHead} alt="img-header" /> */}
        </header>
    )
}
