import React from 'react';
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({

    pcompany:{
        color: 'hsl(180, 29%, 50%)',
        fontSize:'14px',
        fontWeight:'700'
    },

    pposition:{
        fontSize:'16px',
        paddingTop:'12px',
        fontWeight:'900',
    },

    pdays:{
        fontSize:'14px',
        paddingTop:'10px',
        color: 'hsl(180, 8%, 52%)'
    }

    
});


export const Details = ({
    company,
    newFeat,
    featured,
    position,
    dateAt,
    contract,
    location
}) => {

    
    const {pdays,pposition,pcompany} = useStyles();
    console.log(newFeat);

    return (

      
            
            <Grid  item md={4} xs={12} >
                <h3 className={pcompany}>
                    {company}
                </h3>
                <p className={pposition}>
                    {position}
                </p>
                <p className={pdays}>  
                    {dateAt} ・ {contract} ・ {location}
                </p>
            </Grid>
       
            
     

        
    )
}
