import React, { useEffect, useState } from 'react';
import { JobCard } from './components/JobCard';
import dataJob from './data/data.json';
import { Container,Grid } from '@material-ui/core';
import { Header } from './components/Header';
import { Filter } from './components/Filter';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles( them => {

    return{

        cardGrid:{
            
            padding:'15px',
            marginTop:'50px',
            backgroundColor: 'hsl(0, 0%, 98%);',
            boxShadow: '0px 6px 19px -8px hsl(180, 29%, 50%)',
            borderRadius: '5px',
            [them.breakpoints.down('sm')]:{
                padding:'20px'
             }
        },

        featuredActive:{
            borderLeft:'4px solid hsl(180, 29%, 50%)'
        }


    
    }

});

export const JobListingApp = () => {

    const {} = useStyles();
    const [jobList, setJobList] = useState([]);
    const [filters, setFilters] = useState([]);
    
    useEffect(() => {
        
        setJobList(dataJob);

    }, []);


   

    const handleFilter = ({role,level,languages,tools}) => {

        
        const buttons = [role,level,...languages, ...tools];
        
        if(filters.length === 0){
            return true;
        }

        return filters.every((tag) => buttons.includes(tag));
    }
  

   const filteredJobs = jobList.filter(handleFilter);


   const handleClick = (buttons) => {
       if(!filters.includes(buttons)){
        setFilters([...filters,buttons]);
       }
      
   }
 

    return (

        <>

            <Header/> 

            <Container >

                <Grid container style={{display:'flex', border:'1px solid red'}}>
                    {
                        filters.length > 0 && filters.map(
                            (filter) => <Filter key={filter} fil={filter}></Filter>  )
                    }

                    <Grid item md={3} style={{display:'flex', border:'1px solid red'}}>
                        <p>Clear</p>
                    </Grid>
                </Grid>
                                        
            </Container>


            <Container style={{margin:'100px auto',padding:'0px 30px'}}>
                
                {
                    filteredJobs.map(jobs => 
                        <JobCard  key={jobs.id}  job={jobs} handleClick={handleClick} />
                    )
                   // onClick={handleClick} 
                }


            </Container>

        </>
       
    )
}
