import React from 'react'
import { Grid,Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Image } from './Image';
import { Details } from './Details';
import { Skills } from './Skills';


const useStyles = makeStyles({

    cardGrid:{
      
        marginTop:'50px',
    
        backgroundColor: 'hsl(180, 31%, 95%)',
        boxShadow: '0px 6px 19px -8px hsl(180, 29%, 50%)',
        borderRadius: '5px',
      
    },

    b1:{
        border:'1px solid red'
    }

    ,b2:{
        border:'1px solid blue'
    }

    ,b3:{
        border:'1px solid green'
    }
 
    
});


export const JobCard = ({job}) => {

 
    const classes = useStyles();
    const {company,logo,newFeat,role,level,tools,featured,position,location,contract,postedAt,languages} = job;

    const skills = [...role,...level,...languages,...tools];

    console.log(skills);


    return (
        <div>
 
 
            <Box >
                <Grid  className={classes.cardGrid} container direction="row" alignItems="center" spacing={6}  >

                    <Image  path={logo} desc={company}/>
                    <Details 
                            company={company} 
                            newFeat={newFeat} 
                            featured={featured}
                            position={position}
                            dateAt={postedAt}
                            contract={contract}
                            location={location}
                    />

                    {
                        languages.map(lang => 
                            <Skills  languages={lang} />
                        )
                        
                    }

                   

                    {/* <Grid item md={4} xs={12} >
                        <p>
                        {contract}
                        </p>
                    </Grid>

                    <Grid item md={4} xs={12} >
                        <p>
                        {contract}
                        </p>
                    </Grid>

                    <Grid item md={4} xs={12} >
                        <p>
                        {contract}
                        </p>
                    </Grid> */}

                </Grid>
            </Box>
            
            
           

        </div>
    )
}
