import React from 'react';
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({

    pcompany:{
        display:'flex',
        flexDirection:'row',
        alignItems:'center',
        gap:'20px',
        color: 'hsl(180, 29%, 50%)',
        fontSize:'14px',
        fontWeight:'700'
    },

    pposition:{
        fontSize:'16px',
        paddingTop:'12px',
        fontWeight:'900',
    },

    pdays:{
        fontSize:'14px',
        paddingTop:'10px',
        color: 'hsl(180, 8%, 52%)'
    },

    spanNew:{
        borderRadius:'20px',
        padding:'5px',
        fontSize:'12px',
        border:'1px solid red'
    },

    spanFeat:{

    }

    
});


export const Details = ({
    company,
    newFeat,
    featured,
    position,
    dateAt,
    contract,
    location
}) => {

    
    const {pdays,pposition,pcompany,spanNew,spanFeat} = useStyles();

    return (

      
            
            <Grid item md={3} xs={12} >
                <p className={pcompany}>
                    {company}
                    {newFeat &&  <span className={spanNew} > NEW!</span> }
                    {featured &&  <span className={spanFeat}> FEATURED</span> }
                </p>
                <p className={pposition}>
                    {position}
                </p>
                <p className={pdays}>  
                    {dateAt} ・ {contract} ・ {location}
                </p>
            </Grid>
       
            
     

        
    )
}
