import React from 'react'
import { Grid,Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Image } from './Image';
import { Details } from './Details';
import { Skills } from './Skills';


const useStyles = makeStyles({

    cardGrid:{
      
        marginTop:'50px',
    
        backgroundColor: 'hsl(0, 0%, 98%);',
        boxShadow: '0px 6px 19px -8px hsl(180, 29%, 50%)',
        borderRadius: '5px',
      
    },

});


export const JobCard = ({job}) => {

 
    const classes = useStyles();
    const {company,logo,newFeat,role,level,tools,featured,position,location,contract,postedAt,languages} = job;

 
    return (
        <div>
           
 
  
                <Grid  className={classes.cardGrid} container direction="row" alignItems="center"  >

                    <Image  path={logo} desc={company}/>
                    <Details 
                            company={company} 
                            newFeat={newFeat} 
                            featured={featured}
                            position={position}
                            dateAt={postedAt}
                            contract={contract}
                            location={location}
                    />

                    <Skills lang={languages} tools={tools} role={role} level={level} /> 

                </Grid>
       
            
            
           

        </div>
    )
}
